import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ExerciseList from './components/ExerciseList.js';
import WorkoutGenerator from './components/WorkoutGenerator.js';
import Navigation from './components/Navigation.js';
import Workout from './components/Workout.js';
import { Box, Grommet } from 'grommet';


const theme = {
  global: {
    background: "brand",
    colors: {
      brand: "#4C4CA6",
      accent: [
        "#b35959",
        "#59b359"
      ]
    },
    input: {
      border: {
        radius: "4px"
      }
    },
    font: {
      family: 'Spartan',
    },
    text: {
      large: {
        size: "50px",
      },
    },
    nav: {
      text: {
        color: "accent",
      }
    },
    button: {
      border: {
        radius: "4px"
      }
    },
    checkBox: {
      border: {
        radius: "4px"
      }
    },
    layer: {
      border: {
        radius: "4px",
      }
    }
  }
};

function App() {

  return (
    <Grommet theme={theme}>
      <Router>
        <Navigation />
        <Switch>
          <Route path="/list">
            <Box pad="medium">
              <ExerciseList />
            </Box>
          </Route>          
          <Route path="/workout">
            <Box pad="medium">
              <WorkoutGenerator />
            </Box>
          </Route>          
          <Route path="/start">
            <Box pad="medium">
              <Workout />
            </Box>
          </Route>
        </Switch>
      </Router>
    </Grommet>
  );
}

export default App;
