import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ExerciseList from './components/ExerciseList.js';
import WorkoutGenerator from './components/WorkoutGenerator.js';
import Navigation from './components/Navigation.js';
import Workout from './components/Workout.js';
import { Box, Grommet } from 'grommet';


const theme = {
  global: {
    background: "brand",
    colors: {
      brand: "#4C4CA6",
      accent: [
        "#b35959",
        "#59b359"
      ]
    },
    input: {
      border: {
        radius: "4px"
      }
    },
    font: {
      family: 'Montserrat',
    },

    button: {
      border: {
        radius: "4px"
      }
    },
    checkBox: {
      border: {
        radius: "4px"
      }
    },
    layer: {
      border: {
        radius: "4px",
      }
    }
  },
  text: {
    large: {
      size: '40px',
      height: '128px',
    },
    medium: {
      size: '30px',
      height: '20px'
    }
  },
  table: {
    body: {
      pad: { horizontal: 'small', vertical: 'small' },
      border: 'horizontal',
    },
    header: {
      border: 'bottom',
      weight: 'bold',
      font: {
        weight: "bold", 
        color: "red",
      },
      fill: 'horizontal',
      pad: { horizontal: 'small', vertical: 'medium' },
      verticalAlign: 'bottom',
      background: {
        color: 'accent-1',
        opacity: 'strong',
      },
    },
  },
};

function App() {

  return (
    <Grommet theme={theme}>
      <Router>
        <Navigation />
        <Switch>
          <Route path="/list">
            <Box pad="medium">
              <ExerciseList />
            </Box>
          </Route>          
          <Route path="/workout">
            <Box pad="medium">
              <WorkoutGenerator />
            </Box>
          </Route>          
          <Route path="/start">
            <Box pad="medium">
              <Workout />
            </Box>
          </Route>
        </Switch>
      </Router>
    </Grommet>
  );
}

export default App;
