import './ExerciseGrid.scss';
import { useEffect, useState, useCallback } from 'react';
import DataGrid, { SortableHeaderCell }  from 'react-data-grid';

function ExerciseGrid() {

  const [editable, setEditable] = useState(true)

  const columns = [
    { key: 'id', name: 'ID' },
    { key: 'name', name: 'Name' },
    { key: 'type', name: 'Type' },
    { key: 'timing', name: 'Timing', editable: state.editable },
    { key: 'target', name: 'Target' }
  ];

  const [list, setList] = useState([]);

  useEffect(()=>{
    fetch('http://localhost:3000/workouts')
      .then(response => response.json())
      .then(setList);
  }, []);

  let people = list;

  const [dataSource, setDataSource] = useState(people)

  const onEditComplete = useCallback(({ value, columnId, rowIndex }) => {
    const data = [...dataSource];
    data[rowIndex][columnId] = value;

    setDataSource(data)
  }, [dataSource])

  return (
    <div className="ExerciseGrid">
      <SortableHeaderCell />
         <DataGrid 
          columns={columns} 
          rows={list} 
          onEditComplete={onEditComplete}
          editable
          // enableCellSelect={true}
        />
    </div>
  );
}

export default ExerciseGrid;
