// import { useState } from 'react';

function deleteItem(props) {
  let url = `http://localhost:3000/workouts/${ props.id }`;

  fetch(url, { method: 'DELETE' });
  window.location.reload(false);
}

function editItem(props) {
  var editInput = document.getElementById("form" + props.id);
  var editRow = document.getElementById("row" + props.id);

  if (editInput.style.display === "block") {
    editInput.style.display = "none";
    editRow.style.display = "flex";
  } else {
    editInput.style.display = "block";
    editRow.style.display = "none";
  }
}

function updateItem( props ) {
  let url = `http://localhost:3000/workouts/${ props.id }`;

  let section = document.getElementById(props.id);
  let timing = section.querySelector("#timing").value;
  if (timing === "" ) { timing = props.timing }

  let body = {
    id: props.id,
    name: props.name,
    type: props.type,
    timing: timing,
    target: props.target
  };

  fetch(url, {
    method: 'PUT',
    headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
    },
    body: JSON.stringify(body)
  });

  window.location.reload(false);
}

function Exercise( props ) {  

  return (
    <tr id={props.id}>
      <td>{ props.name }</td>
      <td>{ props.type }</td>
      <td>{ props.target }</td>
      <td onClick={() => editItem(props)}>
        <div id={`row${props.id}`}>{ props.timing }</div>

        <form className="exercise-form" id={`form${props.id}`} target="formframe" onSubmit={() => updateItem(props)} >
              <input className="cell inputTiming" type="text" id="timing" name="timing" placeholder={props.timing} />
            </form> 
      </td>
      <td><button onClick={() => deleteItem(props)}>Ã—</button></td>
    </tr>    
  );
}

export default Exercise;
