import { Route } from 'react-router-dom'
import { useEffect, useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { Box, Button as GrommetButton, Grid, Grommet, Header, Nav, ResponsiveContext, Select, Text, TextInput } from 'grommet';

import { grommet } from 'grommet/themes';
import { deepMerge } from 'grommet/utils';

import './WorkoutGenerator.scss';

const customTheme = deepMerge(grommet, {
  select: {
    extend: () => `
      font-size: 40px;
      color: red;
    `,
  },
});

function setWorkoutTypeInputOptions(optionss) {
  // console.log(optionss);
}

function WorkoutGenerator() {

  let options = ["Stretch", "Exercise"];

  let typeExercises = [];
  let targetExercises = [];
  const [workoutTypeInput, setWorkoutTypeInput] = useState('');
  const [workoutTargetInput, setWorkoutTargetInput] = useState('');
  const [workoutRepsInput, setWorkoutRepsInput] = useState('');
  const [type, setType] = useState([]);
  const [targets, setTarget] = useState([]);

  useEffect(()=>{
    fetch('http://localhost:3000/types')
      .then(response => response.json())
      .then(setType);
  }, []);

  useEffect(()=>{
    fetch('http://localhost:3000/target')
      .then(response => response.json())
      .then(setTarget);
  }, []);

  type.map((element, index) => {
    return typeExercises[index] = element.name;
  });  

  targets.map((element, index) => {
    return targetExercises[index] = element.name;
  });

  const size = useContext(ResponsiveContext);


  return (
    <div className="workout-generator">
      <Header justify="end">
        <Nav>
          <Link to='/list'>
            <Text size="large">âš™</Text>
          </Link>
        </Nav>
      </Header>      
      <Box 
        className="workout-generator-form"
        pad="xlarge" >
        <Text
          size="xlarge"
          textAlign="center"
          margin="large" >
          Generate Workout
        </Text>
        <Grid
          className="workout-select"
          fill
          columns={['medium', 'medium', 'xsmall', 'small']}
          justifyContent="center"
          gap="small" >
          <Select
            theme={customTheme}
            placeholder="Exercise Type"
            multiple
            closeOnChange={false}
            options={options}
            onChange={event => setWorkoutTypeInput(event.value)}
          />            
          <Select
            placeholder="Exercise Target"
            border={{
              color: 'dark-3',
              size: 'xsmall',
              style: 'solid',
              side: 'bottom',
            }}
            options={["One", "Two", "Three"]}
            onChange={event => setWorkoutTargetInput(event.value)}
          />        
          <TextInput 
            dropProps={{ height: 'small' }}
            className="workout-input"
            placeholder="Reps" 
            value={workoutRepsInput}
            onChange={event => setWorkoutRepsInput(event.target.value)}
            type="number"
          />
          <Route render={({ history }) => (
            <GrommetButton
              label="Start Workout" 
              onClick={() => { 
                  history.push( {
                    pathname: '/start',
                    state: { 
                      workoutType: workoutTypeInput, 
                      workoutTarget: workoutTargetInput, 
                      workoutReps: workoutRepsInput }
                  });
                }} >
              </GrommetButton>
            )} 
          />
        </Grid>
      </Box>
    </div>
  );
}

export default WorkoutGenerator;
