import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ExerciseList from './components/ExerciseList.js';
import WorkoutGenerator from './components/WorkoutGenerator.js';
import Navigation from './components/Navigation.js';
import Workout from './components/Workout.js';
import { Box, Grommet, Header, Main } from 'grommet';


const theme = {
  global: {
    background: "brand",
    colors: {
      brand: "#4C4CA6",
      accent: [
        "#b35959",
        "#59b359"
      ]
    },
    input: {
      border: {
        radius: "4px"
      }
    },
    font: {
      family: 'Spartan',
    },
    nav: {
      text: {
        color: "accent",
      }
    },
    button: {
      border: {
        radius: "4px"
      }
    },
    checkBox: {
      border: {
        radius: "4px"
      }
    },
    layer: {
      border: {
        radius: "4px",
      }
    }
  }
};

function App() {

  return (
    <Grommet theme={theme}>
      <Router>
        <Switch>
          <Header background="light-4">
            <Navigation />
          </Header>
          <Main pad="small">
          <Route path="/list">
            <ExerciseList />
          </Route>          
          <Route path="/workout">
            <WorkoutGenerator />
          </Route>          
          <Route path="/start">
            <Workout />
          </Route>
          </Main>

        </Switch>
      </Router>
    </Grommet>
  );
}

export default App;
