import './ExerciseGrid.scss';
import { useEffect, useState } from 'react';
import DataGrid  from 'react-data-grid';

function ExerciseGrid() {

  const columns = [
    { key: 'id', name: 'ID', editable: true },
    { key: 'name', name: 'Name', editable: true },
    { key: 'type', name: 'Type', editable: true },
    { key: 'timing', name: 'Timing', editable: true },
    { key: 'target', name: 'Target', editable: true }
  ];

  const [list, setList] = useState([]);

  useEffect(()=>{
    fetch('http://localhost:3000/workouts')
      .then(response => response.json())
      .then(setList);
  }, []);




  const [dataSource, setDataSource] = useState(people);

  const onEditComplete = useCallback(({ value, columnId, rowIndex }) => {
    const data = [...dataSource];
    data[rowIndex][columnId] = value;

    setDataSource(data);
  }, [dataSource])


  return (
    <div className="ExerciseGrid">
         <DataGrid 
          columns={columns} 
          rows={list} 
          onEditComplete={onEditComplete}
          editable={true}
        />
    </div>
  );
}

export default ExerciseGrid;
