import { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

function Workout( props ) {
  const {state} = useLocation();
  const {workoutType, workoutTarget, workoutReps } = state;
  const [query, setQuery] = useState([]);
  const [workoutArray, setWorkoutArray] = useState([]);

  let url = `http://localhost:3000/workouts?type=${workoutType}&target=${workoutTarget}`;
  useEffect(()=>{
    fetch(url)
      .then(response => response.json())
      .then(setQuery);
  }, [url]);

  for (var i = 0; i < workoutReps; i++) {
    let randomNumber = Math.floor(Math.random() * 3 + 1);
    // workoutArray[i] = {...workoutArray[i], "count": "0"};
    workoutArray[i] = {...query[randomNumber], "count": 0};
    console.log(workoutArray[i]);
  } 

  workoutArray.sort(function (a, b) {
    if (a.name < b.name) {
      return -1;
    }
    else if (a.firstname > b.firstname) {
      return 1;
    }
    else {
      return 0;
    }
  });

  for (var k = 1; k < workoutArray.length; k++) {
    if(workoutArray[k].name == workoutArray[k - 1].name) {
      for (var r = 0; r < workoutArray.length; r++) {
        if(workoutArray[r].name == workoutArray[k].name) {
          workoutArray[k].count++;
        }
      }
    }
    console.log();
  } 


  function WorkoutList() { 
    setWorkoutArray(workoutArray);
    return (
      <ul>
        {
          workoutArray.map((item) => {
            if (item) {
              return <li>{item.name} : {item.count}</li>
            }
          })
        }
      </ul>);
  }

  return (
    <div>
      <p>{ workoutReps }</p>
      <div>{ <WorkoutList /> }</div>
    </div>
  );
}

export default Workout;
