import { useEffect, useState } from 'react';




function WorkoutGenerator() {

  const [inputType, setInputType] = useState([]);
  inputType="Stretch";
  const [query, setQuery] = useState([]);
  let url = `http://localhost:3000/workouts?type=${inputType}&target=Posture`;

  function updateItem( ) {

    setInputType(document.querySelector("#test").value);

    // window.location.reload(false);
  }

  useEffect(()=>{
    fetch(url)
      .then(response => response.json())
      .then(setQuery);
  }, []);

  return (
    <div className="workout-generator">
      <h2>Generate Workout</h2>

      <form target="formframe" onSubmit={ () => updateItem() }>
        <input className="cell" type="text" id="test" name="test" />
        <input className="cell" type="submit" placeholder="Submit" />
      </form> 

      {query.map(item =>
        <p key={ item.id }>{ item.name }: {item.timing }</p>
      )}

    </div>
  );
}

export default WorkoutGenerator;
