import './ExerciseList.scss';
import Exercise from './Exercise';
import { useEffect, useState } from 'react';

function ExerciseList() {

  const [list, setList] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/workouts')
      .then(response => response.json())
      .then(setList);
  }, []);

  function refreshPage() {
    window.location.reload(false);
    console.log("refresh");
  }

  return (
    <div className="ExerciseList">
            <table>
        <thead>
          <tr>
            <th>Id</th>
            <th>Name</th>
            <th>Type</th>
            <th>Timing</th>
            <th>Target Area</th>
          </tr>
        </thead>
        <tbody>
        </tbody>
        </table>
        { 
        list.map(item =>
            <Exercise 
              key={ item.id }
              id={ item.id } 
              name={ item.name } 
              type={ item.type } 
              timing={ item.timing } 
              target={ item.target } 
            />
        ) }


        <br />
        <br />

        <iframe name="dummyframe" id="dummyframe" title="dummyframe"></iframe>


        <form action="http://localhost:3000/workouts" method="POST" target="dummyframe" onSubmit={ () => refreshPage() }>
          <input type="text" id="id" name="id" placeholder="Id" /> 
          &nbsp;&nbsp;
          <input type="text" id="name" name="name" placeholder="Name" />
          &nbsp;&nbsp;          
          <input type="text" id="type" name="type" placeholder="Type" />
          &nbsp;&nbsp;         
          <input type="text" id="timing" name="timing" placeholder="Timing" />
          &nbsp;&nbsp;
          <input type="text" id="target" name="target" placeholder="Target" />
          &nbsp;&nbsp;
          <br />
          <br />
          <input type="submit" placeholder="Submit" />
        </form> 

    </div>
  );
}

export default ExerciseList;
