import { useEffect, useState } from 'react';
import Exercise from './Exercise.js';
import './ExerciseList.scss';
import WorkoutGenerator from './WorkoutGenerator.js';

function ExerciseList() {

  const [list, setList] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/workouts')
      .then(response => response.json())
      .then(setList);
  }, []);

  const [type, setType] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/types')
      .then(response => response.json())
      .then(setType);
  }, []);

  const [targets, setTarget] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/target')
      .then(response => response.json())
      .then(setTarget);
  }, []);

  function refreshPage() {
    // window.location.reload(false);
  }

  return (
    <div className="exercise-list">

      <div className="exercise-header">
        <div className="heading">Name</div>
        <div className="heading">Type</div>
        <div className="heading">Timing</div>
        <div className="heading">Target</div>
      </div>

      {list.map(item =>
        <Exercise 
          key={ item.id }
          id={ item.id } 
          name={ item.name } 
          type={ item.type } 
          timing={ item.timing } 
          target={ item.target } 
        />)}

        <iframe name="formframe" id="formframe" title="formframe"></iframe>

        <form id="input" autoComplete="off" action="http://localhost:3000/workouts" method="POST" target="formframe" onSubmit={ () => refreshPage() }>
          <input type="text" id="name" name="name" placeholder="Name" />
          <select id="type" name="type">
            {type.map(item =>
              <option key={item.id} value={item.name}>{item.name}</option>
            )}
          </select>
          <input type="text" id="timing" name="timing" placeholder="Timing" />
          <select id="target" name="target">
            { targets.map(item =>
              <option key={item.id} value={item.name}>{item.name}</option>
            )}
          </select>
          <input type="submit" placeholder="Submit" />
        </form> 

        <br></br>
        <br></br>

        <WorkoutGenerator />

    </div>
  );
}

export default ExerciseList;
