import { Route } from 'react-router-dom'
import { useEffect, useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { Box, Button as GrommetButton, Grid, Header, Nav, ResponsiveContext, Select, Text, TextInput } from 'grommet';

const customTheme = deepMerge(grommet, {
  global: {
    input: {
      padding: {
        horizontal: 'small',
        vertical: 'medium',
      },
    },
  },
  textInput: {
    extend: () => `
      font-size: 20px;
      background: #c9c19f;
      width: 300px;
      margin: 0 auto;

      &:focus {
        box-shadow: none;
        border-color: initial;
      }
    `,
    container: {
      extend: () => `
        background: #edf7d2;
        height: 100px;
        width: 400px;
        display: flex;
        flex-flow: column;
        justify-content: center;
        border-radius: 10px;
      `,
    },
    placeholder: {
      extend: () => `
        width: 100%;
        color: #1e1a11;
      `,
    },
    suggestions: {
      extend: () => `
        background: #c9c19f;
        color: #3d3522;
        li {
          border-bottom: 1px solid rgba(0, 0, 0, 0.2);
        }
      `,
    },
  },
});

function setWorkoutTypeInputOptions(optionss) {
  // console.log(optionss);
}

function WorkoutGenerator() {

  let options = ["Stretch", "Exercise"];

  let typeExercises = [];
  let targetExercises = [];
  const [workoutTypeInput, setWorkoutTypeInput] = useState('');
  const [workoutTargetInput, setWorkoutTargetInput] = useState('');
  const [workoutRepsInput, setWorkoutRepsInput] = useState('');
  const [type, setType] = useState([]);
  const [targets, setTarget] = useState([]);

  useEffect(()=>{
    fetch('http://localhost:3000/types')
      .then(response => response.json())
      .then(setType);
  }, []);

  useEffect(()=>{
    fetch('http://localhost:3000/target')
      .then(response => response.json())
      .then(setTarget);
  }, []);

  type.map((element, index) => {
    return typeExercises[index] = element.name;
  });  

  targets.map((element, index) => {
    return targetExercises[index] = element.name;
  });

  const size = useContext(ResponsiveContext);


  return (
    <div>
      <Header justify="end">
        <Nav>
          <Link to='/list'>
            <Text size="large">⚙</Text>
          </Link>
        </Nav>
      </Header>      
      <Box pad="xlarge" >
        <Text
          size="large"
          textAlign="center"
          margin="medium" >
          Generate Workout
        </Text>
        <Grid
          fill
          columns={['medium', 'medium', 'xsmall', 'small']}
          justifyContent="center"
          gap="small" >
          <Select
            placeholder="Exercise Type"
            multiple
            closeOnChange={false}
            options={options}
            // onChange={({ value: nextValue }) => setValue(nextValue)}
            // value={workoutTypeInput}
            onChange={event => setWorkoutTypeInput(event.value)}
            // onChange={event => setWorkoutTypeInputOptions(event)}
          />        
          <TextInput
            placeholder="Exercise Target"
            value={workoutTargetInput}
            onSelect={event => setWorkoutTargetInput(event.suggestion)}
            suggestions={targetExercises}
          />
          <TextInput 
            placeholder="Reps" 
            value={workoutRepsInput}
            onChange={event => setWorkoutRepsInput(event.target.value)}
            type="number"
          />
          <Route render={({ history }) => (
            <GrommetButton
              label="Start Workout" 
              onClick={() => { 
                  history.push( {
                    pathname: '/start',
                    state: { 
                      workoutType: workoutTypeInput, 
                      workoutTarget: workoutTargetInput, 
                      workoutReps: workoutRepsInput }
                  });
                }} >
              </GrommetButton>
            )} 
          />
        </Grid>
      </Box>
    </div>
  );
}

export default WorkoutGenerator;
