import { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';


function Workout( props ) {
  const [query, setQuery] = useState([]);
  const {state} = useLocation();
  const {workoutType, workoutTarget, workoutReps } = state;

  let url = `http://localhost:3000/workouts?type=${workoutType}&target=${workoutTarget}`;
  useEffect(()=>{
    fetch(url)
      .then(response => response.json())
      .then(setQuery);
  }, [url]);

  let workoutArray = [];

  for (var i = 0; i < 10; i++) {
    let randomNumber = 1; // Math.floor(Math.random() * 3 + 1);
    workoutArray.push(query[randomNumber]);
  } 

  console.log(workoutArray[0]);

  return (
    <div>
      <p>{ workoutReps }</p>

    </div>
  );
}

export default Workout;
