import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ExerciseList from './components/ExerciseList.js';
import WorkoutGenerator from './components/WorkoutGenerator.js';
import Navigation from './components/Navigation.js';
import Workout from './components/Workout.js';
import { Box, Grommet } from 'grommet';


const theme = {
  global: {
    colors: {
      brand: "#4C4CA6",
      accent: [
        "#b35959",
        "#59b359"
      ]
    },
    input: {
      border: {
        radius: "4px"
      }
    },
    font: {
      family: 'Spartan',
    },
    nav: {
      text: {
        color: "accent",
      }
    },
    button: {
      border: {
        radius: "4px"
      }
    },
    checkBox: {
      border: {
        radius: "4px"
      }
    },
    layer: {
      border: {
        radius: "4px",
      }
    }
  }
};

function App() {

  return (
    <Grommet theme={theme}>
      <Box 
        direction="column"
        border={{ color: 'brand', size: 'large' }}
        pad="medium"
      >
        <Router>
          <Navigation />
            <Switch>
              <Route path="/list">
                <ExerciseList />
              </Route>          
              <Route path="/workout">
                <WorkoutGenerator />
              </Route>          
              <Route path="/start">
                <Workout />
              </Route>
            </Switch>
        </Router>
      </Box>
    </Grommet>
  );
}

export default App;
