import { useEffect, useState } from 'react';
import './Exercise.scss';

function deleteItem(props) {
  let url = `http://localhost:3000/workouts/${ props.id }`;

  fetch(url, { method: 'DELETE' });
  window.location.reload(false);
}

function editItem(props) {
  var editInput = document.getElementById("form" + props.id);
  var editRow = document.getElementById("row" + props.id);

  if (editInput.style.display === "block") {
    editInput.style.display = "none";
    editRow.style.display = "flex";
  } else {
    editInput.style.display = "block";
    editRow.style.display = "none";
  }
}

function updateItem( props ) {
  let url = `http://localhost:3000/workouts/${ props.id }`;

  let section = document.getElementById(props.id);
  let timing = section.querySelector("#timing").value;
  if (timing === "" ) { timing = props.timing }

  let body = {
    id: props.id,
    name: props.name,
    type: props.type,
    timing: timing,
    target: props.target
  };

  fetch(url, {
    method: 'PUT',
    headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
    },
    body: JSON.stringify(body)
  });

  window.location.reload(false);
}

function Exercise( props ) {  

  const [type, setType] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/types')
      .then(response => response.json())
      .then(setType);
  }, []);

  const [target, setTarget] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/target')
      .then(response => response.json())
      .then(setTarget);
  }, []);

  return (
    <div className="exercise-container">
      <table>
      <tr>
        <th>Company</th>
        <th>Contact</th>
        <th>Country</th>
        <th>Country</th>
        <th>Country</th>
      </tr>
      <tr>
        <td>{ props.name }</td>
        <td>{ props.type }</td>
        <td onClick={() => editItem(props)} id={`row${props.id}`}>
          { props.timing }
        </td>
        <td>Germany</td>
        <td>Germany</td>
      </tr>
      </table>
      <div className="exercise-wrapper" id={props.id}>
        <div className="exercise exercise-columns">
            <div className="cell name"></div>
            <div className="cell type"></div>
            <div className="cell timing">


              <form className="exercise-form" id={`form${props.id}`} target="formframe" onSubmit={() => updateItem(props)} >
                <input className="cell inputTiming" type="text" id="timing" name="timing" placeholder={props.timing} />
              </form> 

            </div>
            <div className="cell target">{ props.target}</div>
        </div>


      </div>

      <div className="delete-button">
        <button onClick={() => deleteItem(props)}>Ã—</button>
      </div>

    </div>
  );
}

export default Exercise;
