import { grommet } from 'grommet/themes';
import { deepMerge } from 'grommet/utils';
import { ThemeContext } from 'grommet';

const customTheme = deepMerge(grommet, {
  global: {
    focus: {
      border: {
        color: 'red',
      },
      shadow: {
        color: 'red',
      },
    },
  },
});

function ThemeContextTest() {
  return customTheme;
};



export default ThemeContextTest;
