import { useEffect, useState } from 'react';
import Exercise from './Exercise.js';
import AddWorkout from './AddWorkout.js';
import { Link } from 'react-router-dom';
import { Box, Grid, Header, Nav, Table, TableBody, TableCell, TableHeader, TableRow, Text } from 'grommet';

function ExerciseList() {

  let typeExercises = [];
  let targetExercises = [];
  const [type, setType] = useState([]);
  const [targets, setTarget] = useState([]);
  let [urlUpdated, setUrlUpdated] = useState('');
  const [list, setList] = useState([]);

  useEffect(()=>{
    fetch('http://localhost:3000/types')
      .then(response => response.json())
      .then(setType);
  }, []);

  useEffect(()=>{
    fetch('http://localhost:3000/target')
      .then(response => response.json())
      .then(setTarget);
  }, []);

  useEffect(()=>{
    fetch('http://localhost:3000/workouts')
      .then(response => response.json())
      .then(setList);
  }, [urlUpdated]);

  type.map((element, index) => {
    return typeExercises[index] = element.name;
  });  

  targets.map((element, index) => {
    return targetExercises[index] = element.name;
  });

  return (
    <div>

      <Box align='end'>
        <Link to='/workout'>
          <Text size="xlarge">⌂</Text>
        </Link>
      </Box> 

      <Box pad={{left: 'small', bottom: 'medium'}} >
        <Text size="large" >
            Exercise List
        </Text>
      </Box>

      <Box pad={{horizontal: 'xsmall', bottom: 'medium'}} >
        <Table>
          <TableHeader>
            <TableRow>
              <TableCell>
                <Text size="medium">
                  Exercise Name
                </Text>
              </TableCell>
              <TableCell>
                <Text size="medium" >
                  Type
                </Text>
              </TableCell>
              <TableCell>
                <Text size="medium" >
                  Target
                </Text>
              </TableCell>
              <TableCell>
                <Text size="medium" >
                  Timing
                </Text>
              </TableCell>
            </TableRow>
          </TableHeader>
          <TableBody>
            {list.map(item =>
              <Exercise 
                key={ item.id }
                id={ item.id } 
                name={ item.name } 
                type={ item.type } 
                timing={ item.timing } 
                target={ item.target } 
                setUrlUpdated = { setUrlUpdated }
              />
            )}
          </TableBody>
        </Table>
      </Box>

      <Box>
      <AddWorkout setUrlUpdated = { setUrlUpdated } />

      </Box>      

    </div>
  );
}

export default ExerciseList;
