import { useEffect, useState } from 'react';
import { Button as GrommetButton, TextInput } from 'grommet';
import './AddWorkout.scss';


const onChange = () => {
}

function addExerciseToList(exerciseNameInput, exerciseTypeInput, exerciseTargetInput, exerciseTimingInput) {
  let url = `http://localhost:3000/workouts/`;

  let body = {
    name: exerciseNameInput,
    type: exerciseTypeInput,
    timing: exerciseTargetInput,
    target: exerciseTimingInput
  };

  fetch(url, {
    method: 'POST',
    headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
    },
    body: JSON.stringify(body)
  });
}

function AddWorkout( props ) {  
  function refreshPage() {
    window.location.reload(false);
  }

  const [type, setType] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/types')
      .then(response => response.json())
      .then(setType);
  }, []);

  const [targets, setTarget] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/target')
      .then(response => response.json())
      .then(setTarget);
  }, []);

  const typeExercises = ["Stretch", "Exercise"];
  const targetExercises = ["Arms", "Legs", "Core"];

  const [exerciseNameInput, setExerciseNameInput] = useState('');
  const [exerciseTypeInput, setExerciseTypeInput] = useState('');
  const [exerciseTargetInput, setExerciseTargetInput] = useState('');
  const [exerciseTimingInput, setExerciseTimingInput] = useState('');

  console.log(exerciseNameInput + " " + exerciseTypeInput + " " + exerciseTargetInput + " " + exerciseTimingInput);

  return (
    <div>
      <h2>Add Exercise</h2>
      <iframe name="formframe" id="formframe" title="formframe"></iframe>
      <form id="input" autoComplete="off" action="http://localhost:3000/workouts" method="POST" target="formframe" onSubmit={() => refreshPage()}>
        <input type="text" id="name" className="inputName" name="name" placeholder="Name" />
        <select id="type"className="inputType"  name="type">
          {type.map(item =>
            <option key={item.id} value={item.name}>{item.name}</option>
          )}
        </select>
        <select id="target" className="inputTarget" name="target">
          {targets.map(item =>
            <option key={item.id} value={item.name}>{item.name}</option>
          )}
        </select>
        <input type="text" id="timing" className="inputTiming" name="timing" placeholder="Timing" />
        <input type="submit" className="inputSubmit" value="Test" />

        <div className="add-workout-wrapper">
          <TextInput 
            placeholder="Exercise Name" 
            value={exerciseNameInput}
            onChange={event => setExerciseNameInput(event.target.value)}
            className="add-workout-cell"
          />
          <TextInput
            placeholder="Exercise Type"
            value={exerciseTypeInput}
            onSelect={event => setExerciseTypeInput(event.suggestion)}
            suggestions={typeExercises}
            className="add-workout-cell"
          />        
          <TextInput
            placeholder="Exercise Target"
            value={exerciseTargetInput}
            onSelect={event => setExerciseTargetInput(event.suggestion)}
            suggestions={targetExercises}
            className="add-workout-cell"
          />
          <TextInput 
            placeholder="Timing" 
            value={exerciseTimingInput}
            onChange={event => setExerciseTimingInput(event.target.value)}
            className="add-workout-cell" 
          />
          <GrommetButton 
            label="Add Exercise" 
            onClick={() => addExerciseToList(exerciseNameInput, exerciseTypeInput, exerciseTargetInput, exerciseTimingInput)} 
          />
        </div>

      </form> 
    </div>
  );
}

export default AddWorkout;
