import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ExerciseList from './components/ExerciseList.js';
import WorkoutGenerator from './components/WorkoutGenerator.js';
import Workout from './components/Workout.js';
import { Box, Grommet } from 'grommet';

let theme = {

  checkBox: {
    size: "40px",
  },

  breakpoints: {
    small: {
      value: 768,
      text: {
        xlarge: {
          size: '4px',
          height: '5px',
        },
        large: {
          size: '3px',
          height: '4px',
        },
        medium: {
          size: '2px',
          height: '2px',
        }
      },
      flexDirection: "column",
      borderSize: {
        xsmall: "1px",
        small: "2px",
        medium: "4px",
        large: "6px",
        xlarge: "12px"
      },
      edgeSize: {
        none: "0px",
        hair: "1px",
        xxsmall: "2px",
        xsmall: "3px",
        small: "6px",
        medium: "12px",
        large: "24px",
        xlarge: "48px"
      },
      size: {
        xxsmall: "24px",
        xsmall: "48px",
        small: "96px",
        medium: "192px",
        large: "384px",
        xlarge: "768px",
        full: "100%"
      }
    },
    medium: { value: 1536 },
    large: {}
  },
  button: {
    border: {
      radius: "50px",
      width: "6px",
    }
  },
  global: {
    size: {
      medium: "275px",
    },
    background: "brand",
    colors: {
      brand: "#4C4CA6",
      accent: [
        "#b35959",
        "#59b359"
      ],
      backgroundLight: "#d7d7d7",
    },
    input: {
      border: {
        radius: "4px"
      }
    },
    font: {
      family: 'Montserrat',
    },


    checkBox: {
      border: {
        radius: "4px"
      }
    },
    layer: {
      border: {
        radius: "4px",
      }
    }
  },
  text: {
    xlarge: {
      size: '42px',
      height: '50px',
    },
    large: {
      size: '30px',
      height: '40px',
    },
    medium: {
      size: '20px',
      height: '20px',
    }
  },
  table: {
    body: {
      pad: { horizontal: 'small', vertical: 'small' },
      border: 'horizontal',
    },
    header: {
      border: 'bottom',
      weight: 'bold',
      font: {
        weight: "bold", 
        color: "red",
      },
      fill: 'horizontal',
      pad: { horizontal: 'small', vertical: 'medium' },
      verticalAlign: 'bottom',
      background: {
        color: 'accent-1',
        opacity: 'strong',
      },
    },
  },
};

function App() {

  return (
    <Grommet theme={theme}>
      <Router>
        <Switch>
          <Route path="/list">
            <Box  
              margin={{
                top: "18px",
                left: "20px",
                right: "20px",
              }}
            >
              <ExerciseList />
            </Box>
          </Route>          
          <Route path="/workout">
            <Box  
              margin={{
                top: "18px",
                left: "20px",
                right: "20px",
              }}>
              <WorkoutGenerator />
            </Box>
          </Route>          
          <Route path="/start">
            <Box   
              margin={{
                top: "18px",
                left: "20px",
                right: "20px",
              }}>
              <Workout />
            </Box>
          </Route>
        </Switch>
      </Router>
    </Grommet>
  );
}

export default App;
