import React from 'react';
import ReactDOM from 'react-dom';
import App from './App.js'
import './index.scss'
import { Grommet } from 'grommet';


const theme = {

  // textInput: {
  //   extend: () => `
  //     font-size: 18px;
  //     font-weight: normal;
  //     color: purple;
  //     background: #fff;
  //     border: 1px solid #ffdbe1;

  //     &:focus {
  //       box-shadow: none;
  //       border-color: initial;
  //     }
  //   `,
  // },

  global: {

    breakpoints: {
      small: {
        value: 568,
        text: {
          large: {
            size: '4px',
            height: '50px',
          },
        },
      },
      large: {
        value: 1024,
        text: {
          large: {
            size: '4px',
            height: '50px',
          },
        },
      },

      },

      colors: {
        selected: '#ffdbe1',
        brand: 'purple',
        color: 'white',
        border: '#ffdbe1',
      },
      focus: {
        border: {
          color: '#fff00',
        },
      },
      select: {
        control: {
          extend: 'padding: 3px 6px;',
          open: {
            background: '#fff',
            border: '1px solid #7D4CDB'
          }
        }
      },





    size: {
      medium: "275px",
    },

    // colors: {
    //   brand: "#4C4CA6",
    //   accent: [
    //     "#b35959",
    //     "#59b359"
    //   ],
    //   backgroundLight: "#d7d7d7",
    // },

    font: {
      family: 'Montserrat',
    },

  },

  button: {
    extend: () => `
      font-size: 18px;
      font-weight: normal;
      color: purple;
      // border-radius: 50px;
      background: #fff;
      border-color: pink;

      &::placeholder {
        color: purple;
      }

      svg {
        fill: purple;
        stroke: purple;
      }
    `,
  },

  text: {
    xlarge: {
      size: '42px',
      height: '50px',
    },
    large: {
      size: '30px',
      height: '50px',
    },
    medium: {
      size: '20px',
      height: '30px',
    },
    extend: () => `
      font-weight: normal;
      color: purple;

      &::placeholder {
        color: purple;
      }
    `,
  },

  table: {
    body: {
      pad: { horizontal: '0px', vertical: '0px' },
    },
    header: {
      pad: { horizontal: '0px', vertical: 'xsmall' },
    },
    tableCell: {
      text: {
          lineHeight: '10px',
          color: 'red',
      }
    },
  },

  checkBox: {
    size: "50px",
    check: {
      radius: "6px"
    },
    check: {
      extend: () => `
        background-color: white;
        `,
    },
    border: {
      color: '#ffdbe1',
    },
    hover: {
      border: {
        color: 'purple',
      }
    }
  },

  // button: {
  //   // extend: `border-radius: 60px`,
  //   border: {
  //     color: 'accent',
  //   }
  // },

  textInput: {
    extend: () => `
      font-size: 18px;
      font-weight: normal;
      color: purple;
      background: #fff;
      // border: 1px solid #ffdbe1;

      &::placeholder {
        color: purple;
      }
    `,
  },
};


ReactDOM.render(
    <React.StrictMode>
      <Grommet 
      theme={theme}
      >
        <App />
      </Grommet>
    </React.StrictMode>,
  document.getElementById('root')
);
