import { useEffect, useState } from 'react';
import Exercise from './Exercise.js';
import AddWorkout from './AddWorkout.js';
import { Box, Grid, Header, Link, Nav, Table, TableBody, TableCell, TableHeader, TableRow, Text } from 'grommet';

function ExerciseList() {

  let typeExercises = [];
  let targetExercises = [];
  const [type, setType] = useState([]);
  const [targets, setTarget] = useState([]);
  let [urlUpdated, setUrlUpdated] = useState('');
  const [list, setList] = useState([]);

  useEffect(()=>{
    fetch('http://localhost:3000/types')
      .then(response => response.json())
      .then(setType);
  }, []);

  useEffect(()=>{
    fetch('http://localhost:3000/target')
      .then(response => response.json())
      .then(setTarget);
  }, []);

  useEffect(()=>{
    fetch('http://localhost:3000/workouts')
      .then(response => response.json())
      .then(setList);
  }, [urlUpdated]);

  type.map((element, index) => {
    return typeExercises[index] = element.name;
  });  

  targets.map((element, index) => {
    return targetExercises[index] = element.name;
  });

  return (
    <div className="exercise-list">
      <Header pad="medium" justify="end" >
      <Nav direction="row">
        <Link to='/list'>
          <Text
            size="large" 
            >
            ⚙
          </Text>
        </Link>
      </Nav>
    </Header>      
      <Text
        size="large"
        textAlign="center"
      >
        Exercises
      </Text>
      <Box fill align="center">
        <Grid
          fill
        >
          <Table
            size="xlarge"
            width="full"
          >
            <TableHeader>
              <TableRow>
                <TableCell>
                  <Text
                    size="medium"
                    // weight="800"
                  >
                    Exercise Name
                  </Text>
                </TableCell>
                <TableCell>
                  <Text
                    size="medium"
                    // weight="800"
                  >
                    Type
                  </Text>
                </TableCell>
                <TableCell>
                  <Text
                    size="medium"
                    // weight="800"
                  >
                    Target
                  </Text>
                </TableCell>
                <TableCell>
                  <Text
                    size="medium"
                    // weight="800"
                  >
                    Timing
                  </Text>
                </TableCell>
                <TableCell></TableCell>
              </TableRow>
            </TableHeader>
            <TableBody>
              {list.map(item =>
                <Exercise 
                  key={ item.id }
                  id={ item.id } 
                  name={ item.name } 
                  type={ item.type } 
                  timing={ item.timing } 
                  target={ item.target } 
                  setUrlUpdated = { setUrlUpdated }
                />
              )}
            </TableBody>
          </Table>
        </Grid>
      </Box>
        <AddWorkout setUrlUpdated = { setUrlUpdated } />
      <Box>

      </Box>



    </div>
  );
}

export default ExerciseList;
