import { useEffect, useState } from 'react';
import Exercise from './Exercise.js';
// import AddWorkout from './AddWorkout.js';
import { Box, Button as GrommetButton, Grid, Table, TableBody, TableCell, TableHeader, TableRow, Text, TextInput } from 'grommet';

function ExerciseList() {

  let typeExercises = [];
  let targetExercises = [];
  const [type, setType] = useState([]);
  const [targets, setTarget] = useState([]);
  const [exerciseNameInput, setExerciseNameInput] = useState('');
  const [exerciseTypeInput, setExerciseTypeInput] = useState('');
  const [exerciseTargetInput, setExerciseTargetInput] = useState('');
  const [exerciseTimingInput, setExerciseTimingInput] = useState('');
  let [urlUpdated, setUrlUpdated] = useState('');
  const [list, setList] = useState([]);

  useEffect(()=>{
    fetch('http://localhost:3000/types')
      .then(response => response.json())
      .then(setType);
  }, []);

  useEffect(()=>{
    fetch('http://localhost:3000/target')
      .then(response => response.json())
      .then(setTarget);
  }, []);

  useEffect(()=>{
    fetch('http://localhost:3000/workouts')
      .then(response => response.json())
      .then(setList);
  }, [urlUpdated]);

  type.map((element, index) => {
    return typeExercises[index] = element.name;
  });  

  targets.map((element, index) => {
    return targetExercises[index] = element.name;
  });


  function addExerciseToList(exerciseNameInput, exerciseTypeInput, exerciseTargetInput, exerciseTimingInput) {
    let url = `http://localhost:3000/workouts/`;

    let body = {
      name: exerciseNameInput,
      type: exerciseTypeInput,
      timing: exerciseTargetInput,
      target: exerciseTimingInput
    };

    fetch(url, {
      method: 'POST',
      headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
      },
      body: JSON.stringify(body)
    });

    setUrlUpdated(body);
  }

  return (
    <div className="exercise-list">
      <Text
        size="large"
        textAlign="center"
      >
        Exercises
      </Text>
      <Box fill align="center">
        <Grid
          fill
        >
          <Table
            size="xlarge"
            width="full"
          >
            <TableHeader>
              <TableRow>
                <TableCell>
                  <Text
                    size="medium"
                    // weight="800"
                  >
                    Exercise Name
                  </Text>
                </TableCell>
                <TableCell>
                  <Text
                    size="medium"
                    // weight="800"
                  >
                    Type
                  </Text>
                </TableCell>
                <TableCell>
                  <Text
                    size="medium"
                    // weight="800"
                  >
                    Target
                  </Text>
                </TableCell>
                <TableCell>
                  <Text
                    size="medium"
                    // weight="800"
                  >
                    Timing
                  </Text>
                </TableCell>
                <TableCell></TableCell>
              </TableRow>
            </TableHeader>
            <TableBody>
              {list.map(item =>
                <Exercise 
                  key={ item.id }
                  id={ item.id } 
                  name={ item.name } 
                  type={ item.type } 
                  timing={ item.timing } 
                  target={ item.target } 
                  setUrlUpdated = { setUrlUpdated }
                />
              )}
            </TableBody>
          </Table>
        </Grid>
      </Box>
      <Box>
        <div>
        <Text
          size="large"
          textAlign="center"
        >
          Add Exercise
        </Text>
        <Grid
          fill
          columns={['small', 'xsmall', 'small', 'xsmall', 'xsmall']}
          rows={['flex']}
          gap="small"
        >
          <TextInput 
            placeholder="Exercise Name" 
            value={exerciseNameInput}
            onChange={event => setExerciseNameInput(event.target.value)}
            className="add-workout-cell"
          />
          <TextInput
            placeholder="Exercise Type"
            value={exerciseTypeInput}
            onSelect={event => setExerciseTypeInput(event.suggestion)}
            suggestions={typeExercises}
            className="add-workout-cell"
          />        
          <TextInput
            placeholder="Exercise Target"
            value={exerciseTargetInput}
            onSelect={event => setExerciseTargetInput(event.suggestion)}
            suggestions={targetExercises}
            className="add-workout-cell"
          />
          <TextInput 
            placeholder="Timing" 
            value={exerciseTimingInput}
            onChange={event => setExerciseTimingInput(event.target.value)}
            className="add-workout-cell" 
          />
          <GrommetButton 
            label="Add" 
            onClick={() => addExerciseToList(exerciseNameInput, exerciseTypeInput, exerciseTargetInput, exerciseTimingInput)} 
          />
      </Grid>
      </div>
      </Box>



    </div>
  );
}

export default ExerciseList;
