import React from 'react';
import ReactDOM from 'react-dom';
import App from './App.js'
import './index.scss'
import { Grommet } from 'grommet';


const theme = {

  textInput: {
    extend: () => `
      font-size: 18px;
      font-weight: normal;
      color: purple;
      background: #fff;
      border: 1px solid #ffdbe1;

      &:focus {
        box-shadow: none;
        border-color: initial;
      }
    `,
  },

  container: {
    extend: () => `
      font-size: 18px;
      font-weight: normal;
      color: purple;
      background: #fff;
      border: 1px solid #ffdbe1;

      &:focus {
        box-shadow: none;
        border-color: initial;
      }
    `,
  },

  global: {




    size: {
      medium: "275px",
    },

    // colors: {
    //   brand: "#4C4CA6",
    //   accent: [
    //     "#b35959",
    //     "#59b359"
    //   ],
    //   backgroundLight: "#d7d7d7",
    // },

    font: {
      family: 'Montserrat',
    },

  },

  text: {
    xlarge: {
      size: '42px',
      height: '50px',
    },
    large: {
      size: '30px',
      height: '40px',
    },
    medium: {
      size: '20px',
      height: '20px',
    }
  },

  // table: {
  //   body: {
  //     border: 'horizontal',
  //     background: 'white',
  //   },
  //   header: {
  //     pad: { horizontal: 'small', vertical: 'medium' },
  //     background: {
  //       color: 'brand',
  //     },
  //   },
  // },

  // checkBox: {
  //   size: "50px",
  //   check: {
  //     radius: "6px"
  //   }
  // },

  // button: {
  //   // extend: `border-radius: 60px`,
  //   border: {
  //     color: 'accent',
  //   }
  // },
};


ReactDOM.render(
    <React.StrictMode>
      <Grommet 
      theme={theme}
      >
        <App />
      </Grommet>
    </React.StrictMode>,
  document.getElementById('root')
);
