import { useEffect, useState } from 'react';
import { Box, Grid, Button as GrommetButton, TextInput } from 'grommet';
import './AddWorkout.scss';


const onChange = () => {
  console.log("changed");
}

function AddWorkout( props ) {  
  function refreshPage() {
    window.location.reload(false);
  }

  const [type, setType] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/types')
      .then(response => response.json())
      .then(setType);
  }, []);

  const [targets, setTarget] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/target')
      .then(response => response.json())
      .then(setTarget);
  }, []);

  const typeExercises = ["Stretch", "Exercise"];
  const targetExercises = ["Arms", "Legs", "Core"];

  return (
    <div>
      <h2>Add Exercise</h2>
      <iframe name="formframe" id="formframe" title="formframe"></iframe>
      <form id="input" autoComplete="off" action="http://localhost:3000/workouts" method="POST" target="formframe" onSubmit={() => refreshPage()}>
        <input type="text" id="name" className="inputName" name="name" placeholder="Name" />
        <select id="type"className="inputType"  name="type">
          {type.map(item =>
            <option key={item.id} value={item.name}>{item.name}</option>
          )}
        </select>
        <select id="target" className="inputTarget" name="target">
          {targets.map(item =>
            <option key={item.id} value={item.name}>{item.name}</option>
          )}
        </select>
        <input type="text" id="timing" className="inputTiming" name="timing" placeholder="Timing" />
        <input type="submit" className="inputSubmit" value="Test" />

        <div className="add-workout-wrapper">
          <Box pad="medium">
          <Grid
            columns={{
              count: 6,
              size: 'auto',
            }}
            gap="small"
          >
              <TextInput 
                placeholder="Exercise Name" 
                onChange={onChange}
                className="add-workout-cell"
              />
              <TextInput
                placeholder="Exercise Type"
                suggestions={typeExercises}
                className="add-workout-cell"
              />        
              <TextInput
                placeholder="Exercise Target"
                suggestions={targetExercises}
                className="add-workout-cell"
              />
              <TextInput 
                placeholder="Timing" 
                onChange={onChange}
                className="add-workout-cell" 
              />
              <GrommetButton 
                label="Default" 
                onClick={() => {}} {...props} 
                className="add-workout-cell"
              />
            </Grid>
          </Box>
        </div>

      </form> 
    </div>
  );
}

export default AddWorkout;
