import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ExerciseList from './components/ExerciseList.js';
import WorkoutGenerator from './components/WorkoutGenerator.js';
import Workout from './components/Workout.js';
import { Box } from 'grommet';
import { useEffect, useState } from 'react';






function App() {

  let typeExercises = [];
let targetExercises = [];
const [type, setType] = useState([]);
const [targets, setTarget] = useState([]);

useEffect(()=>{
  fetch('http://localhost:3000/types')
    .then(response => response.json())
    .then(setType);
}, []);

useEffect(()=>{
  fetch('http://localhost:3000/target')
    .then(response => response.json())
    .then(setTarget);
}, []);

type.map((element, index) => {
  return typeExercises[index] = element.name;
});  

targets.map((element, index) => {
  return targetExercises[index] = element.name;
});

  return (
    <Router>
      <Switch>
        <Route path="/list">
          <Box  
            margin={{
              top: "18px",
              left: "20px",
              right: "20px",
            }}
          >
            <ExerciseList />
          </Box>
        </Route>          
        <Route path="/workout">
          <Box  
            margin={{
              top: "18px",
              left: "20px",
              right: "20px",
            }}>
            <WorkoutGenerator />
          </Box>
        </Route>          
        <Route path="/start">
          <Box   
            margin={{
              top: "18px",
              left: "20px",
              right: "20px",
            }}>
            <Workout 
              typeExercises={typeExercises}
              targetExercises={targetExercises}
            />
          </Box>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
