import './Exercise.scss';
import { useEffect, useState } from 'react';


function deleteItem(props) {
  let url = `http://localhost:3000/workouts/${ props.id }`;

  fetch(url, { method: 'DELETE' });
  window.location.reload(false);
}

function editItem(props) {
  var form = "form" + props.id;
  var row = "row" + props.id;

  var obj = document.getElementById(form);
  var objrow = document.getElementById(row);

  var status = obj.style.display;

  if (status === "block") {
    obj.style.display = "none";
    // objrow.style.display = "flex";
  } else {
    obj.style.display = "block";
    // objrow.style.display = "none";s
  }
}

function updateItem( props ) {
  let url = `http://localhost:3000/workouts/${ props.id }`;

  let section = document.getElementById(props.id);
  console.log(section);

  let name = section.querySelector("#name").value;
  let type = section.querySelector("#type").value;
  let timing = section.querySelector("#timing").value;
  let target = section.querySelector("#target").value;

  if (name === "" ) { name = props.name }
  if (type === "" ) { type = props.type }
  if (timing === "" ) { timing = props.timing }
  if (target === "" ) { target = props.target }

  let body = {
    id: props.id,
    name: name,
    type: type,
    timing: timing,
    target: target
  };

  fetch(url, {
    method: 'PUT',
    headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
    },
    body: JSON.stringify(body)
  });

  window.location.reload(false);
}

function Exercise( props ) {  

  const [type, setType] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/types')
      .then(response => response.json())
      .then(setType);
  }, []);

  return (
    <div className="exercise-wrapper" id={props.id}>
      <div className="exercise" >
        <div id={`row${props.id}`}>
          <div className="cell">{ props.name }</div>
          <div className="cell">{ props.type }</div>
          <div className="cell">{ props.timing }</div>
          <div className="cell">{ props.target} </div>
        </div>
        <div className="delete-button"><button onClick={ () => deleteItem(props) }>×</button><button onClick={ () => editItem(props) }>✎</button></div>
      </div>
      <div className="exercise-form" id={`form${props.id}`}>
        <form 
          target="formframe" 
          onSubmit={ () => updateItem( props ) } >
          <input className="cell" type="text" id="name" name="name" placeholder={ props.name } />
          <select id="type" name="type">
            <option value={props.type}>{props.type}</option>
            {
              type.map(item => 
                props.type != item.name ? <option key={item.id} value={item.name}>{item.name}</option> : <h1>{item.type}</h1>
              )
          }
          </select>
          <input className="cell" type="text" id="type" name="type" placeholder={ props.type } />
          <input className="cell" type="text" id="timing" name="timing" placeholder={ props.timing } />
          <input className="cell" type="text" id="target" name="target" placeholder={props.target } />
          <input className="cell" type="submit" placeholder="Submit" />
        </form> 
      </div>
    </div>
  );
}

export default Exercise;
