import { Route } from 'react-router-dom'
import { useEffect, useState } from 'react';

function WorkoutGenerator() {

  let workoutName = document.querySelector("#workoutName").value;
  let workoutType = document.querySelector("#workoutType").value;
  let workoutTiming = document.querySelector("#workoutTiming").value;
  let workoutTarget = document.querySelector("#workoutTarget").value;

  if (workoutName === "" ) { workoutName = "" }
  if (workoutType === "" ) { workoutType = "" }
  if (workoutTiming === "" ) { workoutTiming = "" }
  if (workoutTarget === "" ) { workoutTarget = "" }

  const [type, setType] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/types')
      .then(response => response.json())
      .then(setType);
  }, []);

  const [targets, setTarget] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/target')
      .then(response => response.json())
      .then(setTarget);
  }, []);

  return (
    <div className="workout-generator">
      <h2>Generate Workout</h2>

      {/* <iframe name="formframe" id="formframe" title="formframe"></iframe> */}
      <form id="input" autoComplete="off" action="http://localhost:3000/workouts" method="POST" target="formframe">
        <input type="text" id="workoutName" className="inputName" name="workoutName" placeholder="Name" />
        <select id="workoutType" className="inputType"  name="workoutType">
          {type.map(item =>
            <option key={item.id} value={item.name}>{item.name}</option>
          )}
        </select>
        <input type="text" id="workoutTiming" className="inputTiming" name="workoutTiming" placeholder="Timing" />
        <select id="workoutTarget" className="inputTarget" name="workoutTarget">
          {targets.map(item =>
            <option key={item.id} value={item.name}>{item.name}</option>
          )}
        </select>
      </form> 

      <Route render={({ history}) => (
        <button onClick={() => { history.push('/start?type=Exercise') }} >
          Start Workout
        </button>
      )} />

    </div>
  );
}

export default WorkoutGenerator;
