import Types from './Types.js';


function WorkoutGenerator() {

  const queryString = window.location.search;

  function refreshPage() {
    // window.location.reload(false);
    document.getElementById("target").innerHTML = parseParms(queryString).target;
  }

  const [targets, setType] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/target')
      .then(response => response.json())
      .then(setType);
  }, []);

  function parseParms(str) {
    var pieces = str.split("&"), data = {}, i, parts;
    // process each query pair
    for (i = 0; i < pieces.length; i++) {
        parts = pieces[i].split("=");
        if (parts.length < 2) {
            parts.push("");
        }
        data[decodeURIComponent(parts[0])] = decodeURIComponent(parts[1]);
    }
    return data;
}

  return (
    <div className="workout-generator">
      <h2>Generate Workout</h2>

      <form autoComplete="off" action="http://localhost:3000/workouts" method="GET" target="workoutlist" >
        <Types />
        <select>
          <option key="" value="" id="targets" name="targets" >Select One</option>
          { targets.map(item =>
            <option key={item.id} value={item.name} id="targets" name="targets" >{item.name}</option>
          )}
        </select>
        <input type="number" id="reps" name="reps" placeholder="Reps" required />
        <input type="submit" placeholder="Submit" />
      </form> 
      <h5 id="target">test</h5>
      <iframe name="workoutlist" id="workoutlist" title="workoutlist"></iframe>

    </div>
  );
}

export default WorkoutGenerator;
