import { Route } from 'react-router-dom'
import { useEffect, useState } from 'react';

function WorkoutGenerator() {

  let section = document.getElementById(props.id);

  // let name = section.querySelector("#name").value;
  // let type = section.querySelector("#type").value;
  // let timing = section.querySelector("#timing").value;
  // let target = section.querySelector("#target").value;

  if (name === "" ) { name = props.name }
  if (type === "" ) { type = props.type }
  if (timing === "" ) { timing = props.timing }
  if (target === "" ) { target = props.target }

  const [type, setType] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/types')
      .then(response => response.json())
      .then(setType);
  }, []);

  const [targets, setTarget] = useState([]);
  useEffect(()=>{
    fetch('http://localhost:3000/target')
      .then(response => response.json())
      .then(setTarget);
  }, []);

  return (
    <div className="workout-generator">
      <h2>Generate Workout</h2>

      {/* <iframe name="formframe" id="formframe" title="formframe"></iframe> */}
      <form id="input" autoComplete="off" action="http://localhost:3000/workouts" method="POST" target="formframe">
        <input type="text" id="name" className="inputName" name="name" placeholder="Name" />
        <select id="type"className="inputType"  name="type">
          {type.map(item =>
            <option key={item.id} value={item.name}>{item.name}</option>
          )}
        </select>
        <input type="text" id="timing" className="inputTiming" name="timing" placeholder="Timing" />
        <select id="target" className="inputTarget" name="target">
          {targets.map(item =>
            <option key={item.id} value={item.name}>{item.name}</option>
          )}
        </select>
      </form> 

      <Route render={({ history}) => (
        <button onClick={() => { history.push('/start?type=Exercise') }} >
          Start Workout
        </button>
      )} />

    </div>
  );
}

export default WorkoutGenerator;
