import { Route, Link } from 'react-router-dom'
import { useEffect, useState } from 'react';
import { Box, Button, Grid, Select, Text, TextInput, ThemeContext } from 'grommet';

function WorkoutGenerator() {


  const [workoutTypeInput, setWorkoutTypeInput] = useState('');
  const [workoutTargetInput, setWorkoutTargetInput] = useState('');
  const [workoutRepsInput, setWorkoutRepsInput] = useState('');

  let typeExercises = [];
  let targetExercises = [];
  const [type, setType] = useState([]);
  const [targets, setTarget] = useState([]);

  useEffect(()=>{
    fetch('http://localhost:3000/types')
      .then(response => response.json())
      .then(setType);
  }, []);

  useEffect(()=>{
    fetch('http://localhost:3000/target')
      .then(response => response.json())
      .then(setTarget);
  }, []);

  type.map((element, index) => {
    return typeExercises[index] = element.name;
  });  

  targets.map((element, index) => {
    return targetExercises[index] = element.name;
  });


  return (
    <div>
      <Box>
        <Link to='/list'>
          <Text size="large">âš™</Text>
        </Link>
      </Box> 

      <Box 
        pad="xlarge" >
        <Text 
          size="large"
          textAlign="center"
          margin="large" >
          Generate Workout
        </Text>

        <Grid
          fill
          columns={['medium', 'medium', 'xsmall', 'small']}
          justifyContent="center"
          gap="small" >

          <ThemeContext.Extend
            value={{



            textInput: {
              extend: () => `
                font-size: 18px;
                font-weight: normal;
                color: purple;
                background: #fff;
                // border: 1px solid #ffdbe1;

                &::placeholder {
                  color: purple;
                }
              `,
            },

            text: {
              extend: () => `
                font-size: 18px;
                font-weight: normal;
                color: purple;

                &::placeholder {
                  color: purple;
                }
              `,
            }
          }}
          >

            <Select
              placeholder="Exercise Type"
              multiple
              messages={{
                multiple: "Multiple"
              }}
              closeOnChange={false}
              options={typeExercises}
              onChange={event => setWorkoutTypeInput(event.value)}
            /> 

            <Select
              className="workout-select"
              placeholder="Exercise Target"
              options={targetExercises}
              onChange={event => setWorkoutTargetInput(event.value)}
            />      
          </ThemeContext.Extend>

          <ThemeContext.Extend
            value={{
              global: {
                focus: {
                  border: {
                    color: 'purple',
                  },
                },
              },
              textInput: {
                extend: () => `
                  font-size: 18px;
                  font-weight: normal;
                  color: purple;
                  background: #fff;
                  border: 1px solid #ffdbe1;

                  &::placeholder {
                    color: purple;
                  }
                `,
              }
            }}
          >   
            <TextInput 
              className="workout-input"
              placeholder="Reps" 
              value={workoutRepsInput}
              onChange={event => setWorkoutRepsInput(event.target.value)}
              type="number"
            />
          </ThemeContext.Extend>

          <ThemeContext.Extend
            value={{
              global: {
                colors: {
                  border: 'blue',
                },
                focus: {
                  shadow: {
                    width: '3px',
                    color: 'blue',
                  }
                }
              },
              button: {
                border: {
                  color: 'purple',
                },
                extend: () => `
                  font-size: 18px;
                  font-weight: normal;
                  color: purple;
                  background: #fff;
                  border: 1px solid #ffdbe1;

                  &::focus {
                    color: purple;
                    box-shadow: 10px 10px 5px grey;
                  }
                `,
              },
          }}
          >
          <Route render={({ history }) => (
            <Button
              label="Start Workout" 
              onClick={() => { 
                  history.push( {
                    pathname: '/start',
                    state: { 
                      workoutType: workoutTypeInput, 
                      workoutTarget: workoutTargetInput, 
                      workoutReps: workoutRepsInput }
                  });
                }} >
              </Button>
            )} 
          />
        </ThemeContext.Extend>
        </Grid>
      </Box>
    </div>
  );
}

export default WorkoutGenerator;
